import React, { useState, useEffect } from 'react';
import UserService from '../service/UserService';
// import './TimeSheet.css';

function TimeSheet() {
    const [timesheets, setTimesheets] = useState([]);
    const [employeeName, setEmployeeName] = useState('');
    const [date, setDate] = useState('');
    const [hoursWorked, setHoursWorked] = useState('');
    const [taskDescription, setTaskDescription] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        fetchTimesheets();
    }, []);

    const fetchTimesheets = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            setError('User not authenticated');
            return;
        }

        try {
            const response = await UserService.fetchTimesheets(token);
            setTimesheets(response);
        } catch (err) {
            setError('Failed to fetch timesheets');
            console.error(err);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        if (!token) {
            setError('User not authenticated');
            return;
        }

        const newTimesheet = {
            employeeName,
            date,
            hoursWorked,
            taskDescription
        };

        try {
            await UserService.createTimesheet(newTimesheet, token);
            fetchTimesheets();
        } catch (err) {
            setError('Failed to add timesheet');
            console.error(err);
        }
    };

    return (
        <div className="timesheet-container">
            <h1>Timesheet Application</h1>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Employee Name:</label>
                    <input
                        type="text"
                        value={employeeName}
                        onChange={(e) => setEmployeeName(e.target.value)}
                    />
                </div>
                <div className="form-group">
                    <label>Date:</label>
                    <input
                        type="date"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                    />
                </div>
                <div className="form-group">
                    <label>Hours Worked:</label>
                    <input
                        type="number"
                        value={hoursWorked}
                        onChange={(e) => setHoursWorked(e.target.value)}
                    />
                </div>
                <div className="form-group">
                    <label>Task Description:</label>
                    <input
                        type="text"
                        value={taskDescription}
                        onChange={(e) => setTaskDescription(e.target.value)}
                    />
                </div>
                <button type="submit">Add Timesheet</button>
            </form>
            <h2>Timesheet Records</h2>
            <ul>
                {timesheets.map((timesheet) => (
                    <li key={timesheet.id}>
                        {timesheet.employeeName} worked {timesheet.hoursWorked} hours on {timesheet.date} for task: {timesheet.taskDescription}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TimeSheet;
