import React, { useState, useEffect } from "react";
import UserService from "../service/UserService";
import "./EmployeeTask.css";

const EmployeeTask = () => {
  const [tasks, setTasks] = useState([]);
  
  const [newTask, setNewTask] = useState({
    employeeName: "",
    task: "",
    activity: "",
    mon: 0,
    tue: 0,
    wed: 0,
    thu: 0,
    fri: 0,
    sat: 0,
    progress: "",
    startDate: "",
    endDate: "",
    date: "",
    status: "pending", // New field for task status
    ourUsers:{
      id: ""
     } // Adding user id to the field
  });
  const [showTable, setShowTable] = useState(true);
  const [profileInfo, setProfileInfo] = useState({});

  useEffect(() => {
    fetchProfileInfo();
    fetchEmployeeTasks();
  }, []);

  const fetchProfileInfo = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await UserService.getYourProfile(token);
      setProfileInfo(response.ourUsers);

      setNewTask((prevTask) => ({
        ...prevTask,
        userId: response.ourUsers.id // Set userId in newTask
      }));

    } catch (error) {
      console.error("Error fetching profile information:", error);
    }
  };

  const fetchEmployeeTasks = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await UserService.fetchEmployeeTasks(token);
      setTasks(response);
    } catch (error) {
      console.error("Error fetching employee tasks:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTask((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem("token");
      const taskWithDates = { ...newTask };
      await UserService.createEmployeeTask(taskWithDates, token);
      fetchEmployeeTasks();
      setNewTask({
        employeeName: "",
        task: "",
        activity: "",
        mon: 0,
        tue: 0,
        wed: 0,
        thu: 0,
        fri: 0,
        sat: 0,
        progress: "",
        startDate: "",
        endDate: "",
        date: "",
        status: "pending",
        userId: profileInfo.id // Reset userId in newTask
      });
    } catch (error) {
      console.error("Error creating employee task:", error);
    }
  };

  const handleClearAll = () => {
    setNewTask({
      employeeName: "",
      task: "",
      activity: "",
      mon: 0,
      tue: 0,
      wed: 0,
      thu: 0,
      fri: 0,
      sat: 0,
      progress: "",
      startDate: "",
      endDate: "",
      date: "",
      status: "...",
      userId: profileInfo.id // Reset userId in newTask
    });
  };

  const handleApproveTask = async (taskId) => {
    try {
      const token = localStorage.getItem("token");
      await UserService.approveEmployeeTask(taskId, token);
      fetchEmployeeTasks();
    } catch (error) {
      console.error("Error approving employee task:", error);
    }
  };



  return (
    <div className="employee-task-container">
      <h1>Employee Tasks</h1>
      <p>Employee Name : {profileInfo.name}</p>
      <p>User ID : {profileInfo.id} </p> 
      
      <div className="date-picker-container">
        <label htmlFor="startDate">From:</label>
        <input
          type="date"
          id="startDate"
          name="startDate"
          value={newTask.startDate}
          onChange={handleChange}
        />
        <label htmlFor="endDate">To:</label>
        <input
          type="date"
          id="endDate"
          name="endDate"
          value={newTask.endDate}
          onChange={handleChange}
        />
      </div>
      <table className="employee-task-table" border="1">
        <thead>
          <tr>
            <th>Employee Name</th>
            <th>Task</th>
            <th>Activity</th>
            <th>Mon</th>
            <th>Tue</th>
            <th>Wed</th>
            <th>Thu</th>
            <th>Fri</th>
            <th>Sat</th>
            <th>Date</th>
            <th>Progress</th>
            <th>Status</th> {/* New column for task status */}
            {profileInfo.role === "ADMIN" && showTable && <th>Action</th>}
          </tr>
        </thead>
        <tbody>
          {profileInfo.role === "ADMIN" && showTable && (
            <>
              {tasks.map((task, index) => (
                <tr key={index}>
                  <td>{task.employeeName}</td>
                  <td>{task.task}</td>
                  <td>{task.activity}</td>
                  <td>{task.mon}</td>
                  <td>{task.tue}</td>
                  <td>{task.wed}</td>
                  <td>{task.thu}</td>
                  <td>{task.fri}</td>
                  <td>{task.sat}</td>
                  <td>{task.date}</td>
                  <td>{task.progress}</td>
                  <td>{task.status}</td> {/* Display task status */}
                  <td>
                    {profileInfo.role === "ADMIN" && (
                      <button onClick={() => handleApproveTask(task._id)}>
                        Approve
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </>
          )}
          <tr>
            <td>
              <input
                type="text"
                name="employeeName"
                value={newTask.employeeName}
                onChange={handleChange}
                placeholder="Employee Name"
              />
            </td>
            <td>
              <input
                type="text"
                name="task"
                value={newTask.task}
                onChange={handleChange}
                placeholder="Task"
              />
            </td>
            <td>
              <input
                type="text"
                name="activity"
                value={newTask.activity}
                onChange={handleChange}
                placeholder="Activity"
              />
            </td>
            <td>
              <input
                type="number"
                name="mon"
                value={newTask.mon}
                onChange={handleChange}
                placeholder="Mon"
              />
            </td>
            <td>
              <input
                type="number"
                name="tue"
                value={newTask.tue}
                onChange={handleChange}
                placeholder="Tue"
              />
            </td>
            <td>
              <input
                type="number"
                name="wed"
                value={newTask.wed}
                onChange={handleChange}
                placeholder="Wed"
              />
            </td>
            <td>
              <input
                type="number"
                name="thu"
                value={newTask.thu}
                onChange={handleChange}
                placeholder="Thu"
              />
            </td>
            <td>
              <input
                type="number"
                name="fri"
                value={newTask.fri}
                onChange={handleChange}
                placeholder="Fri"
              />
            </td>
            <td>
              <input
                type="number"
                name="sat"
                value={newTask.sat}
                onChange={handleChange}
                placeholder="Sat"
              />
            </td>
            <td>
              <input
                type="date"
                name="date"
                value={newTask.date}
                onChange={handleChange}
                placeholder="yyyy-mm-dd"
              />
            </td>
            <td>
              <input
                type="text"
                name="progress"
                value={newTask.progress}
                onChange={handleChange}
                placeholder="Progress"
              />
            </td>
            <td>{newTask.status}</td> {/* Display status for new task */}
          </tr>
        </tbody>
      </table>
      <div className="button-container">
        <button onClick={handleSubmit}>Submit</button>
        <button onClick={handleClearAll}>Clear All</button>
        {profileInfo.role === "ADMIN" && (
          <button onClick={() => setShowTable(!showTable)}>Toggle Table</button>
        )}
      </div>
    </div>
  );
};

export default EmployeeTask;
