import React from 'react';
import { Link } from 'react-router-dom';
import UserService from '../service/UserService';
import './Navbar.css';

function Navbar() {
    const isAuthenticated = UserService.isAuthenticated();
    const isAdmin = UserService.isAdmin();

    const handleLogout = () => {
        const confirmDelete = window.confirm('Are you sure you want to logout this user?');
        if (confirmDelete) {
            UserService.logout();
        }
    };

    return (
        <nav className='navigation'>
            <ul>

                {isAuthenticated && <li><Link to="/profile" style={{color:'white'}}>Profile</Link></li>}
                {isAdmin && <li><Link to="/admin/user-management"style={{color:'white'}}>User Management</Link></li>}

                {isAuthenticated && <li><Link to="/employeetask" style={{color:'white'}}>Employee Task</Link></li>}
                {isAuthenticated && <li><Link to="/" onClick={handleLogout} style={{color:'white'}}>Logout</Link></li>}
            </ul>
        </nav>
    );
}

export default Navbar;
